export const getAuthToken = () => {
    const token = localStorage.getItem('token');
    return token;
};

export const loader = () => {
    return getAuthToken();
};

export const checkAuthLoader = () => {
    const token = getAuthToken();
    if (!token) redirect('/auth')
}
